import React, { useRef } from 'react'
import {
  View,
  StyleSheet,
  Dimensions,
  FlatList,
  Text,
  Image,
  Animated
} from 'react-native'

const { width, height } = Dimensions.get('screen')
const wImg = width * 0.75
const hImg = height * 0.75

const DATA = [
  {
    uri: 'https://azpet.com.vn/wp-content/uploads/2021/06/Husky.jpg'
  },
  {
    uri:
      'data:image/jpeg;base64,'
  },
  {
    uri:
      'data:image/jpeg;base64,'
  },
  {
    uri:
      'data:image/jpeg;base64,'
  }
]

const Screen3 = () => {
  const scrollX = useRef(new Animated.Value(0)).current

  return (
    <View style={styles.container}>
      <View
        style={{
          position: 'absolute',
          width,
          height
        }}
      >
        {DATA.map((item, index) => {
          const inputRange = [
            (index - 1) * width,
            index * width,
            (index + 1) * width
          ]
          const opacity = scrollX.interpolate({
            inputRange,
            outputRange: [0, 1, 0]
          })
          return (
            <Animated.Image
              key={index.toString()}
              style={{ position: 'absolute', width, height, opacity }}
              source={{ uri: item.uri }}
              blurRadius={10}
            />
          )
        })}
      </View>
      <Animated.FlatList
        onScroll={Animated.event(
          [{ nativeEvent: { contentOffset: { x: scrollX } } }],
          {
            useNativeDriver: true
          }
        )}
        data={DATA}
        keyExtractor={(_, index) => index.toString()}
        horizontal
        pagingEnabled
        renderItem={({ item }) => {
          return (
            <View
              style={{
                width,
                flex: 1,
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              <View
                style={{
                  borderRadius: 20,
                  borderColor: 'blue',
                  borderWidth: 5,
                  shadowColor: '#000',
                  shadowOffset: { width: 0, height: 0 },
                  shadowOpacity: 1,
                  shadowRadius: 20,
                  elevation: 20,
                  overflow: 'hidden'
                }}
              >
                <Image
                  source={{ uri: item.uri }}
                  style={{
                    width: wImg,
                    height: hImg,
                    resizeMode: 'cover'
                  }}
                />
              </View>
            </View>
          )
        }}
      />
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  }
})

export default Screen3
